import Pubsub from 'pubsub-js'; //引入消息订阅与发布

export default {
  name: 'BroadCast',

  data() {
    return {};
  },

  methods: {
    async getVideoUrl(id) {
      const data = await this.$http.get('/mv/url', {
        params: {
          id
        }
      });
      console.log(data);
    }

  },

  created() {
    Pubsub.subscribe('getURL', (msgName, data) => {
      this.getVideoUrl(data);
    });
  }

};
